mapper.select:
select
from dormmana LEFT JOIN admin on admin.adminid = dormmana.admin_id LEFT JOIN (SELECT studid,students.departId,departname,major,sclass,grade,education,studname,studsex,studphone1,studphone2,adress,familyphone FROM students left join department ON students.departId = department.departid) as stu_depart on stu_depart.studid = dormmana.stud_id
studid = #{studid,jdbcType=VARCHAR}
and stu_depart.departId = #{departId,jdbcType=VARCHAR}
and stu_depart.departname like '%' #{department.departname,jdbcType=VARCHAR} '%'
and major like '%' #{major,jdbcType=VARCHAR} '%'
and sclass like '%' #{sclass,jdbcType=VARCHAR} '%'
and grade like '%' #{grade,jdbcType=VARCHAR} '%'
and studname like '%' #{studname,jdbcType=VARCHAR} '%'
and studsex = #{studsex,jdbcType=VARCHAR}

select
from dormmana LEFT JOIN admin on admin.adminid = dormmana.admin_id LEFT JOIN (SELECT studid,departname,major,sclass,grade,education,studname,studsex,studphone1,studphone2,adress,familyphone FROM students left join department ON students.departId = department.departid) as stu_depart on stu_depart.studid = dormmana.stud_id
studid = #{studId,jdbcType=VARCHAR}
and dormDesc like '%' #{dormdesc,jdbcType=VARCHAR} '%'
and admin_id = #{adminId,jdbcType=VARCHAR}
and signtime like '%' #{signtime,jdbcType=VARCHAR} '%'

mapper.update:
update dormmana
set dormDesc = #{dormdesc,jdbcType=VARCHAR},
  admin_id = #{adminId,jdbcType=VARCHAR},
  signtime = #{signtime,jdbcType=TIMESTAMP}
where stud_id = #{studId,jdbcType=VARCHAR}
