mapper.select:
select
from educandcomp LEFT JOIN admin on admin.adminid = educandcomp.admin_id LEFT JOIN (SELECT studid,students.departId,departname,major,sclass,grade,education,studname,studsex,studphone1,studphone2,adress,familyphone FROM students left join department ON students.departId = department.departid) as stu_depart on stu_depart.studid = educandcomp.stud_id
studid = #{studid,jdbcType=VARCHAR}
and stu_depart.departId = #{departId,jdbcType=VARCHAR}
and stu_depart.departname like '%' #{department.departname,jdbcType=VARCHAR} '%'
and major like '%' #{major,jdbcType=VARCHAR} '%'
and sclass like '%' #{sclass,jdbcType=VARCHAR} '%'
and grade like '%' #{grade,jdbcType=VARCHAR} '%'
and studname like '%' #{studname,jdbcType=VARCHAR} '%'
and studsex = #{studsex,jdbcType=VARCHAR}

select
from educandcomp LEFT JOIN admin on admin.adminid = educandcomp.admin_id LEFT JOIN admin as sealer on sealer.adminid = educandcomp.sealer LEFT JOIN (SELECT studid,departname,major,sclass,grade,education,studname,studsex,studphone1,studphone2,adress,familyphone FROM students left join department ON students.departId = department.departid) as stu_depart on stu_depart.studid = educandcomp.stud_id
stud_id = #{studId,jdbcType=VARCHAR}
and admin_id = #{adminId,jdbcType=VARCHAR}
and signtime like '%' #{signtime,jdbcType=VARCHAR} '%'

mapper.insert:
insert into educandcomp (stud_id, rentDesc, admin_id, 
  signtime, sealer, sealtime
  )
values (#{studId,jdbcType=VARCHAR}, #{rentdesc,jdbcType=VARCHAR}, #{adminId,jdbcType=VARCHAR}, 
  #{signtime,jdbcType=TIMESTAMP}, #{sealer,jdbcType=VARCHAR}, #{sealtime,jdbcType=TIMESTAMP}
  )
